CREATE Temporary TABLE loc_classic(
	Lat      NUMERIC(19,15) NOT NULL
	,Lng      NUMERIC(18,15) NOT NULL
	,Name     VARCHAR(32) NOT NULL PRIMARY KEY
	,ZoneName VARCHAR(20) NOT NULL
);

Select
	Unhex(Md5(Concat(z.ExpansionId,z.Name,l.Name)))
	,z.Id, l.Name, l.Lat, l.Lng
From loc_classic l
Inner Join Zones z
	On z.Name = l.ZoneName
	And z.ExpansionId = 1;

Drop Table loc_classic;
